// src/components/Experience.js
import React, { useEffect, useRef } from 'react';
import './Experience.css'; // create this file next

const Experience = () => {
  const itemsRef = useRef([]);

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            entry.target.classList.add('reveal');
            observer.unobserve(entry.target); // one-time animation
          }
        });
      },
      { threshold: 0.1 }
    );

    itemsRef.current.forEach((item) => {
      if (item) observer.observe(item);
    });
  }, []);

  const experiences = [
    {
      title: 'Amazon SURE Intern',
      date: '2022 - 2023',
      description:
        'Was 1 of 7 students chosen to ',
    },
    {
      title: 'Fullstack Developer @ Startup X',
      date: '2020 - 2022',
      description:
        'Built and deployed fullstack applications in a fast-paced environment.',
    },
    {
      title: 'Freelancer',
      date: '2018 - 2020',
      description:
        'Delivered web solutions for clients using modern front-end technologies.',
    },
  ];

  return (
    <section id="experience">
      <h2>Experience</h2>
      <div className="timeline">
        {experiences.map((exp, i) => (
          <div
            key={i}
            ref={(el) => (itemsRef.current[i] = el)}
            className="timeline-item hidden"
          >
            <h3>{exp.title}</h3>
            <p className="date">{exp.date}</p>
            <p>{exp.description}</p>
          </div>
        ))}
      </div>
    </section>
  );
};

export default Experience;
