// src/components/Experience.js
import React, { useEffect, useRef } from 'react';

const Experience = () => {
  const itemsRef = useRef([]);

  useEffect(() => {
    // Intersection Observer for timeline reveal
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            entry.target.classList.add('vanishIn'); // from magic.css
            observer.unobserve(entry.target);
          }
        });
      },
      { threshold: 0.1 }
    );

    itemsRef.current.forEach((item) => {
      if (item) {
        observer.observe(item);
      }
    });
  }, []);

  const experiences = [
    {
      title: 'Technical Lead @ Everpress',
      date: '2022 - 2023',
      description:
        'Led a team of developers to build scalable, user-focused features, focusing on data-driven improvements and growth strategies.',
    },
    {
      title: 'Fullstack Developer @ Startup X',
      date: '2020 - 2022',
      description:
        'Developed front-end and back-end solutions for a fast-paced startup, collaborating with design teams to create user-centric interfaces.',
    },
    {
      title: 'Freelance Projects',
      date: '2018 - 2020',
      description:
        'Delivered robust and modern websites for small businesses and personal blogs, focusing on best practices and responsive design.',
    },
  ];

  return (
    <section id="experience">
      <h2>Experience</h2>
      <div className="timeline" style={{ marginTop: '2rem', position: 'relative' }}>
        {experiences.map((exp, idx) => (
          <div 
            key={idx}
            className="timeline-item magictime" 
            style={{ 
              marginBottom: '2rem', 
              opacity: 0, 
              transform: 'translateY(50px)', 
              transition: 'opacity 0.6s, transform 0.6s' 
            }}
            ref={(el) => (itemsRef.current[idx] = el)}
          >
            <h3 style={{ fontSize: '1.2rem', marginBottom: '0.5rem' }}>{exp.title}</h3>
            <p style={{ fontSize: '0.9rem', color: '#555', marginBottom: '0.5rem' }}>
              {exp.date}
            </p>
            <p style={{ marginBottom: '0.5rem' }}>
              {exp.description}
            </p>
          </div>
        ))}
      </div>
    </section>
  );
};

export default Experience;
