// src/components/Experience.js
import React, { useEffect, useRef } from 'react';

const Experience = () => {
  const itemsRef = useRef([]);

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            entry.target.classList.add('magictime', 'vanishIn');
            observer.unobserve(entry.target);
          }
        });
      },
      { threshold: 0.1 }
    );

    itemsRef.current.forEach((item) => {
      if (item) observer.observe(item);
    });
  }, []);

  const experiences = [
    {
      title: 'Technical Lead @ Everpress',
      date: '2022 - 2023',
      description:
        'Led a team of developers to build scalable, user-focused features.',
    },
    {
      title: 'Fullstack Developer @ Startup X',
      date: '2020 - 2022',
      description:
        'Built and deployed fullstack applications in a fast-paced environment.',
    },
    {
      title: 'Freelancer',
      date: '2018 - 2020',
      description:
        'Delivered web solutions for clients using modern front-end technologies.',
    },
  ];

  return (
    <section id="experience">
      <h2>Experience</h2>
      <div className="timeline" style={{ marginTop: '2rem' }}>
        {experiences.map((exp, index) => (
          <div
            key={index}
            ref={(el) => (itemsRef.current[index] = el)}
            className="timeline-item"
            style={{
              opacity: 0,
              transform: 'translateY(50px)',
              transition: 'all 0.6s ease',
              marginBottom: '2rem',
              background: '#f9f9f9',
              padding: '1rem',
              borderRadius: '8px',
              boxShadow: '0 2px 10px rgba(0,0,0,0.05)',
            }}
          >
            <h3>{exp.title}</h3>
            <p style={{ color: '#666', fontSize: '0.9rem' }}>{exp.date}</p>
            <p>{exp.description}</p>
          </div>
        ))}
      </div>
    </section>
  );
};

export default Experience;
