import React, { useRef, useState } from 'react';
import './Projects.css';

const Projects = () => {
  const scrollRef = useRef(null);
  const [activeIndex, setActiveIndex] = useState(null);

  const projects = [
    {
      title: 'AI-Powered Sleep Recommendation System',
      date: 'March 2025 - Present',
      description:
        'Led the development of an AI-powered sleep recommendation system as the team’s lead software engineer...',
    },
    {
      title: 'Group-Payment App',
      date: 'April 2025 - Present',
      description:
        'Designed and developed a full-stack blockchain-based group payment app to solve the social pressure...',
    },
    {
      title: 'Eterna | Reddit Filtering App',
      date: 'February 2025 - March 2025',
      description:
        'Built a full-stack product recommendation app that filters Reddit discussions to surface high-quality suggestions...',
    },
    {
      title: 'Autonomous Vacuum Cleaner',
      date: 'February 2025 - March 2025',
      description:
        'Developed an autonomous vacuum cleaning simulation using the ACT-R cognitive architecture in Python...',
    },
    {
      title: 'Dynamic Memory Allocator',
      date: 'February 2025 - March 2025',
      description:
        'Designed and implemented a custom dynamic memory allocator in C, replicating core functionalities of malloc...',
    },
    {
      title: 'Flight Application',
      date: 'August 2024 - November 2024',
      description:
        'Independently designed and built a fully custom, microservice-based flight application from the ground up...',
    },
    {
      title: '5 Stage Pipeline Processor',
      date: 'October 2024 - December 2024',
      description:
        'Designed and implemented a fully functional 5-stage pipelined CPU using Verilog...',
    },
    {
      title: 'Linear Device',
      date: 'February 2024 - May 2024',
      description:
        'Developed a custom networked MDADM system in C on Linux to remotely manage JBOD devices...',
    },
    {
      title: 'Course Scheduler',
      date: 'October 2023 - December 2023',
      description:
        'Developed a Java-based course scheduler application using a layered architecture...',
    },
  ];

  const scroll = (direction) => {
    if (scrollRef.current) {
      scrollRef.current.scrollBy({
        left: direction === 'left' ? -300 : 300,
        behavior: 'smooth',
      });
    }
  };

  return (
    <section id="projects">
      <h2>Projects</h2>
      <div className={`project-scroll-wrapper ${zoomedIndex !== null ? 'locked' : ''}`}>
        <button className="scroll-btn left" onClick={() => scroll('left')}>
          ⬅️
        </button>
        <div className="projects-container" ref={scrollRef}>
          {projects.map((project, i) => (
            <div
              key={i}
              className={`project-card ${zoomedIndex === i ? 'zoomed' : ''}`}
            >
              <h3>{project.title}</h3>
              <p className="date">{project.date}</p>

              {zoomedIndex === i ? (
                <>
                  <p className="description">{project.description}</p>
                  <button className="more-btn" onClick={() => setZoomedIndex(null)}>
                    Close
                  </button>
                </>
              ) : (
                <button className="more-btn" onClick={() => setZoomedIndex(i)}>
                  Learn More
                </button>
              )}
            </div>
          ))}
        </div>
        <button className="scroll-btn right" onClick={() => scroll('right')}>
          ➡️
        </button>
      </div>
    </section>
  );
};

export default Projects;
