import React, { useRef, useState } from 'react';
import './Projects.css';

const Projects = () => {
  const scrollRef = useRef(null);
  const [activeIndex, setActiveIndex] = useState(null);

  const projects = [
    {
      title: 'AI-Powered Sleep Recommendation System',
      date: 'March 2025 - Present',
      description:
        'Led the development of an AI-powered sleep recommendation system as the team’s lead software engineer, combining behavioral sleep science with modern NLP. Conducted research to extract evidence-based sleep guidelines and built a custom dataset to fine-tune Meta’s LLaMA 3.2 model using LoRA with Hugging Face’s transformers, peft, and PyTorch libraries. Integrated Retrieval-Augmented Generation (RAG) with FAISS to incorporate user history and enhance personalization. Developed a full-stack application with a FastAPI backend and React frontend, enabling real-time user input and recommendation delivery through a modular API-driven architecture.',
    },
    // Add the rest of your projects...
  ];

  const scroll = (direction) => {
    if (scrollRef.current) {
      scrollRef.current.scrollBy({
        left: direction === 'left' ? -300 : 300,
        behavior: 'smooth',
      });
    }
  };

  const toggleCard = (index) => {
    setActiveIndex(index === activeIndex ? null : index);
  };

  return (
    <section id="projects">
      <h2>Projects</h2>
      <div className="project-scroll-wrapper">
        <button className="scroll-btn left" onClick={() => scroll('left')}>
          ⬅️
        </button>
        <div className="projects-container" ref={scrollRef}>
          {projects.map((project, i) => (
            <div
              key={i}
              className={`project-card ${activeIndex === i ? 'expanded' : ''}`}
            >
              <h3>{project.title}</h3>
              <p className="date">{project.date}</p>
              {activeIndex === i && (
                <p className="description">{project.description}</p>
              )}
              <button className="more-btn" onClick={() => toggleCard(i)}>
                {activeIndex === i ? 'Hide' : 'Learn More'}
              </button>
            </div>
          ))}
        </div>
        <button className="scroll-btn right" onClick={() => scroll('right')}>
          ➡️
        </button>
      </div>
    </section>
  );
};

export default Projects;
